import React, { useState, useEffect } from 'react';

function App() {
  const [cards, setCards] = useState([]);
  const [newCard, setNewCard] = useState({ title: '', description: '' });

  const loadCardsFromLocalStorage = () => {
    if (typeof localStorage === 'undefined') {
      console.error('localStorage no está disponible');
    } else {
      const storedCards = localStorage.getItem('cards');
      if (storedCards) {
        console.log('Datos cargados desde localStorage:', storedCards);
        setCards(JSON.parse(storedCards));
      }
    }
  };

  useEffect(() => {
    loadCardsFromLocalStorage();
  }, []);

  useEffect(() => {
    localStorage.setItem('cards', JSON.stringify(cards));
    console.log('Datos guardados en localStorage:', localStorage.getItem('cards'));
  }, [cards]);

  const handleAddCard = () => {
    console.log('Agregar tarjeta...');
    if (newCard.title.trim() === '' || newCard.description.trim() === '') {
      alert('Ups! parece que dejaste algo vacío.');
      return;
    }
    setCards([...cards, { id: cards.length + 1, title: newCard.title, description: newCard.description }]);
    setNewCard({ title: '', description: '' });
  };

  const handleInputChange = (event) => {
    setNewCard({ ...newCard, [event.target.name]: event.target.value });
  };

  return (
    <div>
      <h1>Tarjetas</h1>
      <ul>
        {cards.map((card) => (
          <li key={card.id}>
            <h2>{card.title}</h2>
            <p>{card.description}</p>
          </li>
        ))}
      </ul>
      <form>
        <label>
          Título de la tarjeta:
          <input type="text" name="title" value={newCard.title} onChange={handleInputChange} />
        </label>
        <br />
        <label>
          Descripción de la tarjeta:
          <input type="text" name="description" value={newCard.description} onChange={handleInputChange} />
        </label>
        <br />
        <button type="button" onClick={handleAddCard}>
          Agregar tarjeta
        </button>
      </form>
    </div>
  );
}

export default App;


