import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'https://api.unsplash.com/search/photos'; // API de Unsplash

const Diseño = () => {
  const [descriptions, setDescriptions] = useState([]);
  const [images, setImages] = useState({});

  const handleDescriptionChange = (index, description) => {
    setDescriptions((prevDescriptions) => {
      prevDescriptions[index] = description;
      return prevDescriptions;
    });
  };

  const handleImageSearch = (description) => {
    axios.get(`${API_URL}?query=${description}&client_id=Rh5G7vaeP8mxMyu3TWqnC3qu3SPq1t1rnrcpvRQkBVI`)
      .then((response) => {
        const image = response.data.results[0];
        setImages((prevImages) => {
          prevImages[description] = image.urls.small;
          return prevImages;
        });
      })
      .catch((error) => {
        console.error(error);
      });
  };

  useEffect(() => {
    descriptions.forEach((description) => {
      handleImageSearch(description);
    });
  }, [descriptions]);

  return (
    <section>
      {descriptions.map((description, index) => (
        <div key={index}>
          <input
            type="text"
            name="description"
            value={description}
            onChange={(e) => handleDescriptionChange(index, e.target.value)}
          />
          {images[description] && (
            <img src={images[description]} alt={description} />
          )}
        </div>
      ))}
    </section>
  );
};

export default Diseño;
