/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react';
import styles from './Marketing.module.css'
import axios from 'axios';


const Marketing = () => {

  const [text, setText] = useState('');
  const [wordCloud, setWordCloud] = useState(null);

  useEffect(() => {
    const fetchWordCloud = async () => {
      if (text.trim() === '') return;
      try {
        const response = await axios.post(
          'https://textvis-word-cloud-v1.p.rapidapi.com/v1/textToCloud',
          { text },
          {
            headers: {
              'Content-Type': 'application/json',
              'X-RapidAPI-Key': 'YOUR_RAPIDAPI_KEY',
              'X-RapidAPI-Host': 'textvis-word-cloud-v1.p.rapidapi.com',
            },
          }
        );
        setWordCloud(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchWordCloud();
  }, [text]);

  const handleTextChange = (event) => {
    setText(event.target.value);
  };
  
  return (
    <div className={styles.container}>
      <h1 className={styles.tituloMark}>MARKETING</h1>

      <section className={styles.cabecera}>
      <div class={`${styles.card} card `}>
        <div class={`${styles.cardBody} card-body`}>
          <h5 className={`${styles.cardTitle} card-title`}>Servicio en redes sociales y paginas personales.</h5>
          <p class="card-text">Administramos tus cuentas y aparición en internet.</p>
          <a href="#" >Card link</a>
          <a href="#" >Another link</a>
        </div>
      </div>
      </section>

      <h1>Word Cloud Generator</h1>
      <textarea value={text} onChange={handleTextChange} placeholder="Enter some text" />
      {wordCloud && (
        <div>
          <h2>Word Cloud:</h2>
          <img src={`data:image/svg+xml;utf8,${wordCloud}`} alt="Word Cloud" />
        </div>
      )}

      <section className={styles.marketingContent}>
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed sit amet nulla auctor, vestibulum magna sed, convallis ex.</p>
        <button className={styles.ctaButton}>Learn More</button>
      </section>

    </div>
  );
};

export default Marketing;

