import React, { useState, useRef, useEffect } from 'react';

const Socios = () => {
  const canvasRef = useRef(null);
  const ctx = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [lineWidth, setLineWidth] = useState(5);
  const [lineColor, setLineColor] = useState('black');
  const [lineCap, setLineCap] = useState('round');
  const [lineJoin, setLineJoin] = useState('round');

  useEffect(() => {
    if (canvasRef.current) {
      ctx.current = canvasRef.current.getContext('2d');
    }
  }, [canvasRef]);

  const handleMouseDown = (event) => {
    setDrawing(true);
    if (ctx.current) {
      const rect = canvasRef.current.getBoundingClientRect();
      const x = event.clientX - rect.left;
      const y = event.clientY - rect.top;
      ctx.current.beginPath();
      ctx.current.moveTo(x, y);
    }
  };
  
  const handleMouseMove = (event) => {
    if (drawing && ctx.current) {
      const rect = canvasRef.current.getBoundingClientRect();
      const x = event.clientX - rect.left;
      const y = event.clientY - rect.top;
      ctx.current.lineTo(x, y);
      ctx.current.stroke();
    }
  };
  const handleMouseUp = () => {
    setDrawing(false);
  };

  const handleLineWidthChange = (event) => {
    setLineWidth(event.target.value);
  };

  const handleLineColorChange = (event) => {
    setLineColor(event.target.value);
  };

  const handleLineCapChange = (event) => {
    setLineCap(event.target.value);
  };

  const handleLineJoinChange = (event) => {
    setLineJoin(event.target.value);
  };

  const clearCanvas = () => {
    if (ctx.current) {
      ctx.current.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    }
  };

  return (
    <div>
      <canvas
        ref={canvasRef}
        width={800}
        height={600}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      />
      <div>
        <label>Line Width:</label>
        <input
          type="range"
          min="1"
          max="50"
          value={lineWidth}
          onChange={handleLineWidthChange}
        />
        <label>Line Color:</label>
        <input
          type="color"
          value={lineColor}
          onChange={handleLineColorChange}
        />
        <label>Line Cap:</label>
        <select value={lineCap} onChange={handleLineCapChange}>
          <option value="butt">Butt</option>
          <option value="round">Round</option>
          <option value="square">Square</option>
        </select>
        <label>Line Join:</label>
        <select value={lineJoin} onChange={handleLineJoinChange}>
          <option value="bevel">Bevel</option>
          <option value="round">Round</option>
          <option value="miter">Miter</option>
        </select>
        <button onClick={clearCanvas}>Clear Canvas</button>
      </div>
    </div>
  );
};

export default Socios;
