import React, { useState } from 'react';
import styles from './Home.module.css';
import Nissan from '../assets/img/GTR-WALLPAPER.jpg';

const db = indexedDB.open('carsDB', 1);

db.onupgradeneeded = (event) => {
  const db = event.target.result;
  const objectStore = db.createObjectStore('cars', { keyPath: 'id' });
  objectStore.createIndex('name', 'name', { unique: false });
  objectStore.createIndex('description', 'description', { unique: false });
};

const addCarToDB = (car) => {
  const transaction = db.transaction('cars', 'readwrite');
  const objectStore = transaction.objectStore('cars');
  const request = objectStore.add(car);
  request.onsuccess = () => {
    console.log('Coche agregado con éxito');
  };
  request.onerror = (event) => {
    console.error('Error al agregar coche:', event.target.errorCode);
  };
};

const getCarsFromDB = () => {
  const transaction = db.transaction('cars', 'readonly');
  const objectStore = transaction.objectStore('cars');
  const request = objectStore.getAll();
  request.onsuccess = (event) => {
    const cars = event.target.result;
    setCars(cars);
  };
  request.onerror = (event) => {
    console.error('Error al obtener coches:', event.target.errorCode);
  };
};

const Home = () => {
  //array para los objetos
  const [cars, setCars] = useState(() => {
    const storedCars = localStorage.getItem('cars');
    return storedCars ? JSON.parse(storedCars) : [];
  });

  //array para almacenar nuevos objetos
  const [newCar, setNewCar] = useState({ name: '', description: '' });

  //agrega nuevo objeto desde "newCar" hacia "cars"( luego resetea "newCar")
  const handleAddCar = () => {
    if (newCar.name.trim() === '' || newCar.description.trim() === '') {
      alert('Ups! parece que dejaste algo vacío.');
      return;
    }
    setCars([...cars, { id: cars.length + 1, name: newCar.name, description: newCar.description }]);
    setNewCar({ name: '', description: '' });
    addCarToDB({ id: cars.length + 1, name: newCar.name, description: newCar.description });
  };

  //cambia el valor de "newCar" cuando se modifica el input
  const handleInputChange = (event) => {
    setNewCar({ ...newCar, [event.target.name]: event.target.value });
  };

  const handleDeleteCar = (id) => {
    setCars(cars.filter((car) => car.id !== id));
    saveCarsToLocalStorage(); // Guarda los datos en localStorage
  };

  useEffect(() => {
    getCarsFromDB();
  }, []);

  return (
    <div className={styles.contenedor}>"
      <h1 className={styles.tituloHome}>HOME</h1>
      <p className={styles.parr}>Conoce nuestras cosas.</p>
      
      <section className={styles.tarj}>"
        {/* "map" renderiza una lista de tarjetas para cada lista de coches */}
        
        <div className={styles.card}>"
          <img src={Nissan} alt="Nissan" className={styles.cardImg} />
          <div className={`${styles.cardBody} card-body`}>
            <p>Tarjeta de alguna cosa.</p>
          </div>
        </div>

        <div className={styles.card}>"
          <img src={Nissan} alt="Nissan" className={styles.cardImg} />
          <div className={`${styles.cardBody} card-body`}>
            <p>Tarjeta de alguna cosa.</p>
          </div>
        </div>

        <div className={styles.card}>"
          <img src={Nissan} alt="Nissan" className={styles.cardImg} />
          <div className={`${styles.cardBody} card-body`}>
            <p>Tarjeta de alguna cosa.</p>
          </div>
        </div>

        {cars.map((car) => (
          <div key={car.id} className={styles.card}>"
            <img src={Nissan} className={`${styles.cardImg} card-img-top`} alt={car.name} />
            <div className={`${styles.cardBody} card-body`}>
              <p class="card-text">{car.description}</p>
              <button onClick={() => handleDeleteCar(car.id)}>Borrar</button>
            </div>
          </div>
        ))}
      </section>

      <section className={styles.form}>"
        <h2>Agregar nuevo coche</h2>
        <input
          type="text"
          name="name"
          value={newCar.name}
          onChange={handleInputChange}
          placeholder="Nombre del coche"
        />
        <input
          type="text"
          name="description"
          value={newCar.description}
          onChange={handleInputChange}
          placeholder="Descripción del coche"
        />
        <button onClick={handleAddCar}>Agregar coche</button>
      </section>

      <section className={styles.banner}>"
        <h2>Titulo de una sección</h2>
        <p>Contenido de una sección.</p>
      </section>
    </div>
  );
};

export default Home;





